interface LangSelectorProps {
  lng: string;
  props: {
    en: string;
    es: string;
  };
}

const LangSelector = ({ lng, props }: LangSelectorProps) => {
  const fullPathname = usePathname();
  // PATHWITHOUTLANG
  const splitPath = fullPathname.split("/");
  const currentPath = "/" + splitPath.slice(2).join("/");
  // PATHWITHOUTLANG
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef(null);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleClickOutside = (event: MouseEvent) => {
    if (
      menuRef.current &&
      !(menuRef.current as HTMLElement).contains(event.target as Node)
    ) {
      setMenuOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleClickOutside);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  return (
    <div className="relative inline-block" ref={menuRef}>
      <div>
        <button type="button" onClick={toggleMenu}>
          {lng === "es" ? <MXFlag /> : <USFlag />}
        </button>
      </div>
      {menuOpen && (
        <div
          className="origin-top-right absolute right-0 mt-2 w-32 rounded-md shadow list-none bg-red-white bg-gray-50 dark:bg-gray-700 ring-1 ring-black ring-opacity-5 focus:outline-none"
          aria-roledescription="menu"
        >
          <div className="py-1" aria-roledescription="none">
            <Link
              href={`/es/${currentPath}`}
              className="flex items-center p-2 rounded-lg dark:hover:bg-gray-700 group w-full hover:bg-gray-100 text-gray-900 dark:text-white"
            >
              <MXFlag />
              <span className="ml-2">{props.es}</span>
            </Link>
            <Link
              href={`/en/${currentPath}`}
              className="flex items-center p-2 rounded-lg dark:hover:bg-gray-700 group w-full hover:bg-gray-100 text-gray-900 dark:text-white"
            >
              <USFlag />
              <span className="ml-2">{props.en}</span>
            </Link>
          </div>
        </div>
      )}
    </div>
  );
};

export default LangSelector;
